void DrawBlock(int32 x, int32 y, int32 z, const ColorF&amp; color, double scale = 1.0)
{
	MakeBox(x, y, z).scaled(scale).draw(color);
}

// ブロックを描く関数
void DrawBlock(int32 x, int32 y, int32 z, const ColorF&amp; color, const Texture&amp; blockTexture)
{
