	const P2Body line = world.createLine(P2Static, Vec2{ 0, 0 }, Line{ -1600, 0, 1600, 0 }, OneSided::Yes, P2Material{ 1.0, 0.1, 1.0 });

	// 文字のパーツ
	Array&lt;P2Body&gt; bodies;

	String text;
	int32 generation = 0;
	HashTable&lt;P2BodyID, int32&gt; table;

