		for (int32 i = 0; i &lt; NumKeys; ++i)
		{
			// オフセット値が偶数であるものが白鍵
			if (IsEven(KeyPositions[i]))
			{
				RectF{ (KeyPositions[i] / 2 * KeySize.x), 0, KeySize.x, KeySize.y }
					.stretched(-1).draw(Keys[i].pressed() ? Palette::Pink : Palette::White);
			}
		}

		// 黒鍵を描画する
		for (int32 i = 0; i &lt; NumKeys; ++i)
		{
			// オフセット値が奇数であるものが黒鍵
			if (IsOdd(KeyPositions[i]))
